/** 匹配带 export 的所有行 */
export declare const exportLineRegexp: RegExp;
/**
 * 匹配 export = xxx
 */
export declare const lineExportAllRegExp: RegExp;
/**
 * 匹配 export { a, b as c } from 'xxx';
 *
 * $1 是 " a, b as c "
 *
 * $2 是 "xxx"
 */
export declare const lineExportLocalsFromRegexp: RegExp;
/**
 * 匹配 export * from 'xxx'
 *
 * $1 是 "*"
 *
 * $2 是 'xxx'
 */
export declare const lineExportAllFromRegExp: RegExp;
/** 匹配 export default */
export declare const lineExportDefaultRegexp: RegExp;
/** 匹配的变量的声明，如 export interface xxx,  export class yyy */
export declare const lineExportVariableRegexp: RegExp;
/** 匹配的变量的声明，如 export {a, b as c}，注意和 lineExportLocalsFromRegexp 区分，这里没有 from */
export declare const lineExportLocalsRegexp: RegExp;
/** 匹配带 import 的所有行 */
export declare const importLineRegExp: RegExp;
/** 匹配 import * as xxx from './xxx' */
export declare const lineImportAllRegExp: RegExp;
/**
 * 匹配
 *
 * export namespace xxx {
 *  // ....
 * }
 *
 * 会将 namespace 中的所有 export 清空，所以要保证当前有个同名的 export 存在
 */
export declare const exportNamespaceRegExp: RegExp;
/**
 * 匹配 import xxx from './xxx'
 *
 * 还需要去检查 from 的文件内容是否有 export default，因为 tsconfig 可以开启 allowSyntheticDefaultImports
 */
export declare const lineImportDefaultRegExp: RegExp;
/** 匹配 import {a, b as c} from './xxx' */
export declare const lineImportLocalsRegExp: RegExp;
/** 匹配 import './index.scss' 这种只引用的形式 */
export declare const lineImportOnlyRegExp: RegExp;
/** 匹配 import React, { Component } from 'react'， 需要 tsconfig 开启 allowSyntheticDefaultImports */
export declare const lineImportAllAndLocalsRegExp1: RegExp;
/** 匹配 import { Component }, React from 'react'， 需要 tsconfig 开启 allowSyntheticDefaultImports */
export declare const lineImportAllAndLocalsRegExp2: RegExp;
/**
 * 拆分 locals 里的 "a, b as c" 这种字符串
 *
 * 注意 split 最好前先 trim 字符串，并且确认字符串有值
 */
export declare const splitRegexp: RegExp;
/** */
export declare const asRegexp: RegExp;
/** 放在文件路径后面的分隔符，后面接文件中的变量 */
export declare const KEY_SEPARATOR = "::";
/** 放在 KEY_SEPARATOR 后面，表示导出此文件的所有变量 */
export declare const KEY_ALL = "*";
export declare const KEY_DEFAULT = "default";
