/**
 * 按先后顺序一个个用 run 函数来运行 tasks 中的字段
 *
 * @export
 * @template T
 * @template R
 * @param {T[]} tasks 要运行的任务
 * @param {(task: T) => Promise<R>} run 运行函数
 * @returns {Promise<R[]>} 返回每个 tasks 对应的结果组成的数组
 */
export declare function series<T, R>(tasks: T[], run: (task: T, index: number, tasks: T[]) => Promise<R>): Promise<R[]>;
export declare function map<T, R>(tasks: T[], iterator: (task: T, index: number, ref: T[]) => Promise<R>, limit?: number): Promise<R[]>;
export declare function replace(src: string, gregexp: RegExp, iterator: (mat: RegExpMatchArray) => Promise<string>, limit?: number): Promise<string>;
